import React from 'react';
import svgIcon from '../img/cargo-truck-delivery-svgrepo-com.svg';
import MarketplaceSquares from './MarketplaceSquares'; // Import the MarketplaceSquares component

function HomeHero({ cargoListings }) {
  return (
    <div className="bg-blue-400 p-8 flex items-center justify-center">
      {/* Left side with SVG */}
      <div className="w-1/3">
        <img
          src={svgIcon}
          alt="SVG Image"
          className="w-64 h-64"
        />
      </div>

      {/* Right side with description and marketplace squares */}
      <div className="w-2/3 p-4">
        <h2 className="text-2xl font-bold mb-4 font-mono">All about CargoConnect</h2>
        <p className="text-gray-200">
          CargoConnect is a revolutionary cargo marketplace platform designed to optimize cargo space usage, promote sustainability, and enhance cost-efficiency in the transportation industry.
        </p>

        {/* Display the MarketplaceSquares component with cargo listings */}
        <MarketplaceSquares cargoListings={cargoListings} />
      </div>
    </div>
  );
}

export default HomeHero;
