import React, {useState, useEffect} from "react";
import { auth, db } from './config/firebase';
import { signOut } from 'firebase/auth';
import { useNavigate, useParams } from "react-router-dom";
import TripForm from "./tripForm";
import { getDoc, collection } from "firebase/firestore";

function TruckerPage() {
  const history = useNavigate();
  const { uid } = useParams();

  const handleClick = () => {
    signOut(auth).then(val => {
      history("/");
    });
  };

  const[userTrips, setUserTrips] = useState([]);

  const userCollectionRef = collection(db, "trip");

  const getUserTrips = async () => {
    const data = await getDoc(userCollectionRef);
    console.log(data);
  }

  useEffect(() => {
    getUserTrips();
  }, [])

  return (
    <div>
      <button
        onClick={handleClick}
        className="absolute top-0 right-0 mt-4 mr-4 bg-blue-500 text-white p-2 rounded hover:bg-blue-700"
      >
        Sign Out
      </button>
      <div className="mt-16">
        <TripForm uid={uid}/>
      </div>
    </div>
  );
}

export default TruckerPage;
