import React, { useState } from 'react';

function CargoDetail({ listing, onBookCargo }) {
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setSelectedImage(file);
  };

  const handleBookCargo = () => {
    // You can implement the booking logic here
    // For example, you can send the cargo details and the selected image to the server
    onBookCargo(listing, selectedImage);
  };

  return (
    <div>
      <h2>Cargo Details</h2>
      <div>
        <p>Departure City: {listing.departureCity}</p>
        <p>Arrival City: {listing.arrivalCity}</p>
        <p>Departure Date: {listing.departureDate}</p>
        <p>Arrival Date: {listing.arrivalDate}</p>
        <p>Cargo Space: {listing.cargoSpace} cubic feet</p>
      </div>
      <input type="file" onChange={handleImageUpload} />
      <button onClick={handleBookCargo}>Book Cargo</button>
    </div>
  );
}

export default CargoDetails;
