import React, {useState, useEffect} from "react";
import { auth, db } from './config/firebase';
import { signOut } from 'firebase/auth';
import { useNavigate, useParams } from "react-router-dom";
import TripForm from "./tripForm";
import {collection, query, where, getDocs } from 'firebase/firestore'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendar, faMapMarker, faWeight } from '@fortawesome/free-solid-svg-icons';


function TruckerPage() {
  const history = useNavigate();
  const { uid } = useParams();

  const handleClick = () => {
    signOut(auth).then(val => {
      history("/");
    });
  };

  const[userTrips, setUserTrips] = useState([]);

  const userCollectionRef = collection(db, "trip");


  const getUsers = async () => {
    const q = query(userCollectionRef, where('uid', '==', uid));
    const data = await getDocs(q);
    setUserTrips(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
    console.log("HI")
  };

    useEffect(() => {
        getUsers();    
    }, []);

  return (
    <div>
      <button
        onClick={handleClick}
        className="absolute top-0 right-0 mt-4 mr-4 bg-blue-500 text-white p-2 rounded hover:bg-blue-700"
      >
        Sign Out
      </button>
      <div className="mt-16">
        <TripForm uid={uid} getUsers={getUsers}/>
      </div>
      <div>
      {userTrips.map((data) => {
    return (
        <div key={data.tripId} className="p-4 mx-4 my-2 bg-gradient-to-r from-blue-200 to-blue-300 hover:from-blue-300 hover:to-blue-400 rounded-md shadow-md flex flex-col md:flex-row justify-evenly">
            <div className="mb-2 md:mb-0 md:mr-4">
                <div className="flex items-center mb-1">

                    <p className="text-lg font-semibold">Start Date:</p>
                    <p>{data.startDate}</p>
                </div>
                <div className="flex items-center mb-1">

                    <p className="text-lg font-semibold">End Date:</p>
                    <p>{data.endDate}</p>
                </div>
            </div>
            <div className="mb-2 md:mb-0 md:mr-4">
                <div className="flex items-center mb-1">

                    <p className="text-base font-semibold">Start Location:</p>
                    <p>{data.startLocation}</p>
                </div>
                <div className="flex items-center mb-1">

                    <p className="text-base font-semibold">End Location:</p>
                    <p>{data.endLocation}</p>
                </div>
            </div>
            <div>
                <div className="flex items-center">
                <FontAwesomeIcon icon="fa-sharp fa-light fa-house" />
                    <p className="text-sm font-semibold">Cargo Size:</p>
                    <p>{data.cargoSize}</p>
                </div>
            </div>
        </div>
    );
})
  }
      </div>
    </div>
  );
}

export default TruckerPage;
