import { useParams } from 'react-router-dom';

function CargoDetails() {
  // Use useParams to get the cargo ID from the URL
  const { cargoId } = useParams();

  // Function to handle booking
  const handleBook = (e) => {
    e.preventDefault(); // Prevent any default action (e.g., navigating)

    // You can add logic here to show a confirmation message or perform other actions if needed.
  };

  return (
    <div className="text-center">
      {/* <h2>Cargo Details</h2>
      <p>Cargo ID: {cargoId}</p> */}
      {/* Display cargo details here */}
      <button
        onClick={handleBook}
        className="bg-blue-400 text-white p-4 rounded-full cursor-pointer inline-block"
        style={{ fontSize: '1.5rem', width: '200px', margin: '0 auto' }}
      >
        Book
      </button>
    </div>
  );
}

export default CargoDetails;
