import React, {useState, useEffect} from "react";
import { auth, db } from './config/firebase';
import { signOut } from 'firebase/auth';
import { useNavigate, useParams } from "react-router-dom";
import TripForm from "./tripForm";
import {collection, query, where, getDocs } from 'firebase/firestore'

function TruckerPage() {
  const history = useNavigate();
  const { uid } = useParams();

  const handleClick = () => {
    signOut(auth).then(val => {
      history("/");
    });
  };

  const[userTrips, setUserTrips] = useState([]);

  const userCollectionRef = collection(db, "trip");


  const getUsers = async () => {
    const q = query(userCollectionRef, where('uid', '==', uid));
    const data = await getDocs(q);
    setUserTrips(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
    console.log("HI")
  };

    useEffect(() => {
        getUsers();    
    }, []);

  return (
    <div>
      <button
        onClick={handleClick}
        className="absolute top-0 right-0 mt-4 mr-4 bg-blue-500 text-white p-2 rounded hover:bg-blue-700"
      >
        Sign Out
      </button>
      <div className="mt-16">
        <TripForm uid={uid} getUsers={getUsers}/>
      </div>
      <div>
      {userTrips.map((data) => {
    return (
        <div key={data.tripId} className="border rounded p-4 m-4 bg-gray-200">
            <div className="mb-4">
                <p className="font-semibold">Start Date:</p>
                <p>{data.startDate}</p>
            </div>
            <div className="mb-4">
                <p className="font-semibold">End Date:</p>
                <p>{data.endDate}</p>
            </div>
            <div className="mb-4">
                <p className="font-semibold">Start Location:</p>
                <p>{data.startLocation}</p>
            </div>
            <div className="mb-4">
                <p className="font-semibold">End Location:</p>
                <p>{data.endLocation}</p>
            </div>
            <div>
                <p className="font-semibold">Cargo Size:</p>
                <p>{data.cargoSpace}</p>
            </div>
        </div>
    );
  })
  }
      </div>
    </div>
  );
}

export default TruckerPage;
