import React, {useState} from "react";
import {auth, db} from './config/firebase';
import {signOut} from 'firebase/auth'
import { useNavigate, useParams } from "react-router-dom";
import {setDoc, collection, doc} from 'firebase/firestore'
import TripForm from "./tripForm";

function TruckerPage () {

    const history = useNavigate();

    const {uid} = useParams();

    const userCollectionRef = collection(db, "trip");

    const [input, setInput] = useState({});

    const getInputData = (data) => {
        setInput(data);
    }

    const createTrip = async (db, uid) => {
        try {
            const userDocRef = doc(db, 'trip', uid);
            await setDoc(db, input);
        } catch {
            console.log('Error creating/updating user data');
        }
    }

    const handleClick = () => {
        signOut(auth).then(val => {
            history("/")
        })
    }

    return (
        <div>
            <p>{uid}</p>
            <button onClick={handleClick}>Sign Out</button>
            <TripForm getInputData={getInputData}/>
        </div>
    )
}

export default TruckerPage;
