import React from 'react';
import { useNavigate } from 'react-router-dom';

function MarketplaceSquares({ cargoListings }) {

  function getRandomTruckerName() {
    const names = ['John Doe', 'Jane Smith', 'Mike Johnson', 'Emily Davis', 'Tom Wilson'];
    return names[Math.floor(Math.random() * names.length)];
  }

  const navigate = useNavigate();

  const handleListingClick = (listing) => {
    navigate(`/cargo-booking/${listing.departureCity}-${listing.arrivalCity}`, {
      state: { cargoDetails: listing }
    });
  };

  return (
    <div className="bg-gray-100 p-4 text-center">
      <h2 className="text-2xl font-bold mb-4">Cargo Listings</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-3 xl:grid-cols-3 gap-4 mx-auto max-w-7xl">
        {cargoListings.map((listing, index) => (
          <div
            key={index}
            className="bg-white p-8 rounded-xl shadow-lg"
            onClick={() => handleListingClick(listing)}
          >
            <h3 className="text-lg font-semibold mb-2 underline">{getRandomTruckerName()}</h3>
            <p className="text-gray-700">
              <span className="font-semibold">Departure City:</span> {listing.departureCity}
            </p>
            <p className="text-gray-700">
              <span className="font-semibold">Arrival City:</span> {listing.arrivalCity}
            </p>
            <p className="text-gray-700">
              <span className="font-semibold">Departure Date:</span> {listing.departureDate}
            </p>
            <p className="text-gray-700">
              <span className="font-semibold">Arrival Date:</span> {listing.arrivalDate}
            </p>
            <p className="text-gray-700">
              <span className="font-semibold">Cargo Space:</span> {listing.cargoSpace} cubic feet
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MarketplaceSquares;
