import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

function CargoDetails() {
  const location = useLocation();
  const cargoDetails = location.state?.cargoDetails;
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setSelectedImage(file);
  };

  return (
    <div>
      {cargoDetails ? (
        <div>
          <h2>Cargo Details</h2>
          <p>Departure City: {cargoDetails.departureCity}</p>
          <p>Arrival City: {cargoDetails.arrivalCity}</p>
          {/* Add other cargo details here */}

          {/* Image upload input */}
          <label>Upload an Image</label>
          <input type="file" accept="image/*" onChange={handleImageUpload} />

          {/* Display uploaded image */}
          {selectedImage && (
            <div>
              <p>Uploaded Image:</p>
              <img src={URL.createObjectURL(selectedImage)} alt="Uploaded" />
            </div>
          )}
        </div>
      ) : (
        <p>Cargo details not found</p>
      )}
    </div>
  );
}

export default CargoDetails;
