import React, {useState, useEffect} from "react";
import { auth, db } from './config/firebase';
import { signOut } from 'firebase/auth';
import { useNavigate, useParams } from "react-router-dom";
import TripForm from "./tripForm";
import {collection, query, where, getDocs } from 'firebase/firestore'

function TruckerPage() {
  const history = useNavigate();
  const { uid } = useParams();

  const handleClick = () => {
    signOut(auth).then(val => {
      history("/");
    });
  };

  const[userTrips, setUserTrips] = useState([]);

  const userCollectionRef = collection(db, "trip");

  const getUserTrips = async () => {
    const collectionRef = collection(db, 'yourCollection');

    getDocs(collectionRef)
      .then((querySnapshot) => {
        const newData = [];
        querySnapshot.forEach((doc) => {
          newData.push(doc.data());
        });
        setUserTrips(newData);
      })
      .catch((error) => {
        console.error('Error getting documents: ', error);
      });
}

  useEffect(() => {
    getUserTrips();
  }, [])

  return (
    <div>
      <button
        onClick={handleClick}
        className="absolute top-0 right-0 mt-4 mr-4 bg-blue-500 text-white p-2 rounded hover:bg-blue-700"
      >
        Sign Out
      </button>
      <div className="mt-16">
        <TripForm uid={uid}/>
      </div>
      <div>
      {userTrips.map((data) => {
            return (
                <div>
                    <p>{data.endDate}</p>
                </div>
            );
        })}
      </div>
      <button
        onClick={handleClick}      >
        Sign Hi
      </button>
    </div>
  );
}

export default TruckerPage;
