import React, {useState} from "react";
import {auth, db} from './config/firebase';
import {signOut} from 'firebase/auth'
import { useNavigate, useParams } from "react-router-dom";
import {addDoc, collection} from 'firebase/firestore'
import TripForm from "./tripForm";

function TruckerPage () {

    const history = useNavigate();

    const {uid} = useParams();

    const userCollectionRef = collection(db, "trip");

    const [input, setInput] = useState({});

    const getInputData = (data) => {
        setInput(data);
    }

    const createTrip = async () => {
        await addDoc(userCollectionRef, { ...input, uid: uid });
    }

    const handleClick = () => {
        signOut(auth).then(val => {
            history("/")
        })
    }


    return (
        <div>
            <p>{uid}</p>
            <button onClick={handleClick}>Sign Out</button>
            <button onClick={createTrip}>Add</button>
            <TripForm getInputData={getInputData} uid={uid}/>
        </div>
    )
}

export default TruckerPage;
