import React, { useState } from "react";
import { collection, addDoc } from 'firebase/firestore';
import { db } from './config/firebase'; // Assuming you have Firebase Firestore initialized in 'config/firebase'.

function TruckerPage() {
  const [departureCity, setDepartureCity] = useState("");
  const [arrivalCity, setArrivalCity] = useState("");
  const [departureDate, setDepartureDate] = useState("");
  const [arrivalDate, setArrivalDate] = useState("");
  const [cargoSpace, setCargoSpace] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create a new cargo listing object
    const cargoListing = {
      departureCity,
      arrivalCity,
      departureDate,
      arrivalDate,
      cargoSpace: parseInt(cargoSpace, 10),
    };

    // Add the cargo listing to Firebase Firestore
    const docRef = await addDoc(collection(db, "cargoListings"), cargoListing);
    console.log("Document written with ID: ", docRef.id);

    // Clear the form fields after submission
    setDepartureCity("");
    setArrivalCity("");
    setDepartureDate("");
    setArrivalDate("");
    setCargoSpace("");
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>Departure City:</label>
        <input type="text" value={departureCity} onChange={(e) => setDepartureCity(e.target.value)} required />
        <br />

        <label>Arrival City:</label>
        <input type="text" value={arrivalCity} onChange={(e) => setArrivalCity(e.target.value)} required />
        <br />

        <label>Departure Date:</label>
        <input type="date" value={departureDate} onChange={(e) => setDepartureDate(e.target.value)} required />
        <br />

        <label>Arrival Date:</label>
        <input type="date" value={arrivalDate} onChange={(e) => setArrivalDate(e.target.value)} required />
        <br />

        <label>Cargo Space (cubic feet):</label>
        <input type="number" value={cargoSpace} onChange={(e) => setCargoSpace(e.target.value)} required />
        <br />

        <button type="submit">Add Listing</button>
      </form>
    </div>
  );
}

export default TruckerPage;
